    }
    Else {
        $PicturePath = $PicturePath.replace('"', '')
    }
    $FolderName = $FolderName.replace('"', '')
    $Titletext = $Titletext.replace('"', '')
    if ($MoviePosterCard) {
        $PosterType = "Movie"
    }
    Elseif ($ShowPosterCard) {
        $PosterType = "Show"
    }
    Else {
        $PosterType = "Poster"
    }
    if ($LibraryFolders -eq 'true') {
