            }
            if ($SeasonPosterName -match $seasonNumberPattern) {
                $global:SeasonNumber = $Matches[1]
                $global:seasontmp = "Season" + $global:SeasonNumber.PadLeft(2, '0')
            }
            Elseif ($SeasonPosterName -eq $specialsPattern) {
