                                $seasontemp | Add-Member -MemberType NoteProperty -Name "Download Source" -Value 'false'
                                $seasontemp | Add-Member -MemberType NoteProperty -Name "Manual" -Value "false"
                                switch -Wildcard ($global:FavProvider) {
