                                                                }
                                                                if (!$global:posterurl) {
                                                                    Write-Entry -Subtext "Could not find a TitleCard on any site" -Path $global:ScriptRoot\Logs\Scriptlog.log -Color Red -log Error
                                                                }
                                                            }
                                                        }
                                                        else {
