                                                            $global:posterurl = GetTMDBShowBackground
                                                            if (!$global:posterurl) {
                                                                $global:posterurl = GetFanartShowBackground
                                                            }
                                                            if (!$global:posterurl) {
                                                                $global:IsFallback = $true
                                                                if ($ArtUrl) {
