                    "Episodes"                     = $Episodes
                    "Title"                        = $EpisodeTitles
                    "OtherMediaServerTitleCardTag" = $Thumbs
                    "OtherMediaServerSeasonTag"    = $SeasonEpisodes[0].SeriesPrimaryImageTag
