                                                }

                                                $logEntry = "`"$magick`" $Arguments"
                                                $logEntry | Out-File $global:ScriptRoot\Logs\ImageMagickCommands.log -Append
                                                InvokeMagickCommand -Command $magick -Arguments $Arguments
