                Write-Entry -Subtext "  Found location - '$($location)'" -Path $global:ScriptRoot\Logs\Scriptlog.log -Color Cyan -log Debug
                # Compare lib.Path with each location
                if ($Show.Path -like "$location/*" -or $Show.Path -like "$location\*") {
