                $extraFolder = $pathSegments[0]  # All segments up to the extracted folder
            }
            else {
                $extractedFolder = $pathSegments[0]  # Only one segment, it's the folder
                $extraFolder = $null  # No parent structure
            }

            if ($Show.Tags) {
                $Labels = $($Show.Tags -join ',')
