                                                $global:posterurl = GetTVDBShowPoster
                                                if ($global:posterurl) {
                                                    Write-Entry -Subtext "Using the Show Poster as Season Fallback..." -Path $global:ScriptRoot\Logs\Scriptlog.log -Color Yellow -log Warning
                                                    $global:IsFallback = $true
