                                                            $episodetemp | Add-Member -MemberType NoteProperty -Name "Download Source" -Value $global:posterurl
                                                            $episodetemp | Add-Member -MemberType NoteProperty -Name "Manual" -Value "false"
                                                            switch -Wildcard ($global:FavProvider) {
