                Remove-Item -LiteralPath $dir.FullName -Force -Confirm:$false | Out-Null

                # Log the action
                Write-Entry -Subtext "Removed empty directory: $($dir.FullName)" -Path $global:ScriptRoot\Logs\Scriptlog.log -Color Yellow -log Info
            }
